function openDropdown(a, b, c, d = "40px", e = "62px") {
    $(a).css("left", d), $(a).css("top", e), $(a).css("opacity", 1), $(a).css("visibility", "visible"), c.attr("id", b)
}

function closeDropdown(a, b, c, d = 0, e = "82px") {
    $(a).css("left", d), $(a).css("top", e), $(a).css("opacity", 0), $(a).css("visibility", "hidden"), c.attr("id", b)
}
$(document).on("click", "#settingButton", function() {
    openDropdown("#settingDropdown", "unsettingButton", $(this), "17px")
}), $(document).on("click", "#unsettingButton", function() {
    closeDropdown("#settingDropdown", "settingButton", $(this), "17px")
}), $(document).on("click", "#profileButton", function() {
    leftPx = parseInt($("#isDesktop").val()) ? "40px" : "-5px", topPx = parseInt($("#isDesktop").val()) ? "62px" : "50px", openDropdown("#profileDropdown", "unprofileButton", $(this), leftPx, topPx)
}), $(document).on("click", "#unprofileButton", function() {
    closeDropdown("#profileDropdown", "profileButton", $(this))
});