function setCookie(a, b) {
    const c = new Date;
    c.setFullYear(c.getFullYear() + 10);
    const d = c.toUTCString();
    document.cookie = `${a}=${b}; expires=${d}; max-age=${315360000}; path=/; priority=High;`
}

function getCookie(a) {
    let b = a + "=",
        d = document.cookie.split(";");
    for (let e, c = 0; c < d.length; c++) {
        for (e = d[c];
            " " == e.charAt(0);) e = e.substring(1);
        if (0 == e.indexOf(b)) return e.substring(b.length, e.length)
    }
    return ""
}

function has30MinutesPassed() {
    const a = new Date,
        b = a.getTime();
    if (localStorage.next_show_galak > b) return !1;
    const c = moment(a).add(30, "m").toDate(),
        d = c.getTime();
    return localStorage.next_show_galak = d, !0
}

function setShowAdsCookie() {
    has30MinutesPassed() ? setCookie("should_do_galak", "show") : setCookie("should_do_galak", "hide")
}
setShowAdsCookie();