window.setBg = () => {
    $(".set-bg").each(function() {
        var a = $(this).data("setbg");
        $(this).css("background-image", "url(" + a + ")"), parseInt($("#isDesktop").val()) && $(this).hover(function() {
            $(this).css("background-image", "linear-gradient(rgba(0, 0, 0, 0.527),rgba(0, 0, 0, 0.5)), url(" + a + ")")
        }, function() {
            $(this).css("background-image", "url(" + a + ")")
        })
    })
}, window.setBgV2 = () => {
    $(".set-bg-v2").each(function() {
        var a = $(this).data("setbg");
        $(this).css("background-image", "linear-gradient(rgba(0, 0, 0, 0.527),rgba(0, 0, 0, 0.5)), url(" + a + ")")
    })
};